    var num = split[1] + split[2];
    var closeHour = parseInt(close);
    var openHour = parseInt(open);

    var json = JSON.parse(listaDeHorarios);
    if (json.calendars[calendarId].busy.length == 1) {
        var init = new Date(json.calendars[calendarId].busy[0].start).getHours();
        var final = new Date(json.calendars[calendarId].busy[0].end).getHours();
        if (init == 8 && final == 19) {
            return false;
        }
    }

    json.calendars[calendarId].busy.forEach(function (element) {
        var startHour = new Date(element.start).getHours();
        var endHour = new Date(element.end).getHours();
        startHour = eval(startHour + mathOperation + num);
        endHour = eval(endHour + mathOperation + num);
